[data-webtui-theme='everforest-*-*'] {
    --background0: var(--bg0);
    --background1: var(--bg1);
    --background2: var(--bg2);
    --background3: var(--bg3);

    --foreground0: var(--fg);
    --foreground1: var(--gray2);
    --foreground2: var(--gray1);
}

